INSERT INTO superhero VALUES (695,'1516609548-7178','Black Flash','https://www.superherodb.com/black-flash/10-10831/',45,10,100,80,100,30,'-','No alter egos found.','Barry Allen, Flashback, Slow Lightning, Black Racer, Death Flash, God of Death','-','Flash Vol 2 #138','DC Comics','neutral','Male','God / Eternal','- // 0 cm','- lb // 0 kg','-','-','-','-','Living Assault Weapons,Flash Family','-','','','','Black Flash''s powers','https://www.superherodb.com/black-flash/10-10831/powers/','[{\"powers\":\"Speed Force Entity\"},{\"powers\":\"As the aspect of death for speedsters, the Black Flash draws power from the extra-dimensional energy-field called the Speed Force.\"},{\"powers\":\"Superhuman Speed\"},{\"powers\":\"The Black Flash can move at vast superhuman speeds, easily keeping up with Wally West, and surpassing the speed of light.\"},{\"powers\":\"Superhuman Durability\"},{\"powers\":\"Superhuman Stamina\"},{\"powers\":\"Superhuman Agility\"},{\"powers\":\"Fatal Touch\"},{\"powers\":\"The Black Flash can kill a person with only one touch.\"},{\"powers\":\"Intangibility\"},{\"powers\":\"By vibrating his body at a specific frequency, Black Flash can become intangible, allowing object to pass directly through him.\"},{\"powers\":\"Chronokinesis\"},{\"powers\":\"The Black Flash possesses the ability to stop time at will, as well as use his speed to travel throughout time.\"},{\"powers\":\"Dimensional Travel\"},{\"powers\":\"The Black Flash can easily travel to and from the Speed Force at will.\"},{\"powers\":\"Immorty\"},{\"powers\":\"As an aspect of death, the Black Flash cannot die by any ordinary means.\"},{\"powers\":\"Claws\"}]','','','','','/pictures2/portraits/10/100/10831.jpg');
